import React, { Component } from 'react';
import styles from './css/main.css'

class Name extends Component {
  render () {
    const name = this.props.name;

    return (
      <span style={{color: 'red'}}>{name}</span>
    );
  }
}

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: 'Input your name'
    }
  }

  render() {
    const name = this.state.name;

    return (
      <div className="container row">
        <div>
          <h1 className="input_name"><span style={{color: 'red'}}>{name}</span></h1>
          <input type="text" value={name} onChange={ e  => { this.setState({ name: e.target.value }) }} />
        </div>

        <div>
          <textarea value={this.state.textAreaValue} onChange={this.onChangeText} />
        </div>

        <div>
          <button onClick={this.onClick}>送信</button>
        </div>

      </div>
    );
  }
}

class NewsComponent extends React.Component {
  render(){
    return(
      <div className={styles.news}>
        <ul>
          <li className={styles.new}>お知らせ3</li>
          <li>お知らせ2</li>
          <li>お知らせ1</li>
        </ul>
      </div>
    )
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<NewsComponent />, rootElement);
